$(document).ready(function () {

    if (screen.width > 993) {
        $(".search-form-block__button").click(function () {
            $(".search-form-block, .search-input").toggleClass("search-active");
            $("input[type='text']").focus();
        });
    }
    
    $('.menu-bottom__text, .nav-icon-1').click(function () {
        if ($('.menu-bottom').css('display') == 'none') {
            $('.menu-bottom').slideDown();
            $('.icon').addClass('open');
        } else {
            $('.menu-bottom').slideUp();
            $('.icon').removeClass('open');
        }
    });

    $('.menu-lines').click(function () {
        if ($('.menu-list').css('display') == 'none') {
            $('.menu-list').slideDown();
            $('.burger').addClass('burger-open');
        } else {
            $('.menu-list').slideUp();
            $('.burger').removeClass('burger-open');
        }
    });

    new WOW().init();
    $(window).width(function () {
        $(window).width() < 600 && ($(".wow").removeClass("wow"),
            $(".main-block.fadeInUp").removeClass("fadeInUp"),
            $(".category-selection-block.fadeInUp").removeClass("fadeInUp"))
    });

    $('.footer-audio__button').click(function () {
        var parent = $(this).parent();
        var button = $(this);
        var audio = $('audio', parent)[0];
        var duration = $('.player-example-duration', parent);

        // переключение состояния плеера и смена картинки на кнопке - плей или пауза
        if (audio.paused == false) {
            audio.pause();
            button.css('background', 'url(img/audio.png)');
        } else {
            audio.play();
            button.css('background', 'url(img/play.png)');
        }

        // по окончанию трека на кнопку устанавливается картинка плей
        $(audio).on('ended', function () {
            button.css('background', 'url(img/audio.png)');
        });

        // обновление текущей позиции воспроизведения
        $(audio).on('timeupdate', function () {
            var date = new Date(audio.currentTime * 1000);
            duration.html(date.getMinutes() + ':' + date.getSeconds());
        });
    });

    //Переменная для включения/отключения индикатора загрузки
    var spinner = $('.ymap-container').children('.loader');
    //Переменная для определения была ли хоть раз загружена Яндекс.Карта (чтобы избежать повторной загрузки при наведении)
    var check_if_load = false;
    //Необходимые переменные для того, чтобы задать координаты на Яндекс.Карте
    var myMapTemp, myPlacemarkTemp;

    //Функция создания карты сайта и затем вставки ее в блок с идентификатором &#34;map-yandex&#34;
    function init() {
        var myMapTemp = new ymaps.Map("map-yandex", {
            center: [45.0340, 38.9938], // координаты центра на карте
            zoom: 10, // коэффициент приближения карты
            controls: ['zoomControl', 'fullscreenControl'] // выбираем только те функции, которые необходимы при использовании
        });

        myMapTemp.behaviors.disable('scrollZoom');

        // Получаем первый экземпляр коллекции слоев, потом первый слой коллекции
        var layer = myMapTemp.layers.get(0).get(0);

        // Решение по callback-у для определения полной загрузки карты
        waitForTilesLoad(layer).then(function () {
            // Скрываем индикатор загрузки после полной загрузки карты
            spinner.removeClass('is-active');
        });
    }

    // Функция для определения полной загрузки карты (на самом деле проверяется загрузка тайлов) 
    function waitForTilesLoad(layer) {
        return new ymaps.vow.Promise(function (resolve, reject) {
            var tc = getTileContainer(layer),
                readyAll = true;
            tc.tiles.each(function (tile, number) {
                if (!tile.isReady()) {
                    readyAll = false;
                }
            });
            if (readyAll) {
                resolve();
            } else {
                tc.events.once("ready", function () {
                    resolve();
                });
            }
        });
    }

    function getTileContainer(layer) {
        for (var k in layer) {
            if (layer.hasOwnProperty(k)) {
                if (
                    layer[k] instanceof ymaps.layer.tileContainer.CanvasContainer ||
                    layer[k] instanceof ymaps.layer.tileContainer.DomContainer
                ) {
                    return layer[k];
                }
            }
        }
        return null;
    }

    // Функция загрузки API Яндекс.Карт по требованию (в нашем случае при наведении)
    function loadScript(url, callback) {
        var script = document.createElement("script");

        if (script.readyState) { // IE
            script.onreadystatechange = function () {
                if (script.readyState == "loaded" ||
                    script.readyState == "complete") {
                    script.onreadystatechange = null;
                    callback();
                }
            };
        } else { // Другие браузеры
            script.onload = function () {
                callback();
            };
        }

        script.src = url;
        document.getElementsByTagName("head")[0].appendChild(script);
    }

    // Основная функция, которая проверяет когда мы навели на блок с классом &#34;ymap-container&#34;
    var ymap = function () {
        $('.ymap-container').mouseenter(function () {
            if (!check_if_load) { // проверяем первый ли раз загружается Яндекс.Карта, если да, то загружаем

                // Чтобы не было повторной загрузки карты, мы изменяем значение переменной
                check_if_load = true;

                // Показываем индикатор загрузки до тех пор, пока карта не загрузится
                spinner.addClass('is-active');

                // Загружаем API Яндекс.Карт
                loadScript("https://api-maps.yandex.ru/2.1/?apikey=(4be942cf-24ef-450f-b0fb-2b2f40b3a3b1)&lang=ru_RU", function () {
                    // Как только API Яндекс.Карт загрузились, сразу формируем карту и помещаем в блок с идентификатором &#34;map-yandex&#34;
                    ymaps.load(init);
                });
            }
        });
    }

    $(function () {
        //Запускаем основную функцию
        ymap();

    });

    if (screen.width < 993) {
        $('.click-search svg').click(function (e) {
            e.preventDefault();
            $('.mask, .search-modal').fadeIn();
        });
        $('.mask').click(function () {
            $('.mask, .search-modal').fadeOut();
        });
    }

    $('.delivery-label').click(function () {
        $('.delivery-label').removeClass('border-active');
        $(this).addClass('border-active');
    });

});
